CREATE OR REPLACE PROCEDURE pls_imp_pre_pagto_pck.ident_beneficiarios_imp ( nr_seq_prot_gerado_p pls_faturamento_prot.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) AS $body$
DECLARE

		
nr_seq_segurado_conv_w	pls_faturamento_conta.nr_seq_segurado_conv%type;
		
C01 CURSOR FOR
	SELECT	a.cd_usuario_plano,
			a.nr_sequencia
	from	pls_faturamento_conta a
	where	nr_seq_protocolo = nr_seq_prot_gerado_p;
									
BEGIN

	for r_c01_w in C01 loop
	
		nr_seq_segurado_conv_w :=  pls_obter_segurado_carteira(r_c01_w.cd_usuario_plano, cd_estabelecimento_p);
		
		-- jtrindade OS - 2041630

		if (coalesce(nr_seq_segurado_conv_w::text, '') = '') then
			nr_seq_segurado_conv_w	:= pls_obter_segurado_comp_risco(r_c01_w.cd_usuario_plano);
		end if;
		
		if (nr_seq_segurado_conv_w IS NOT NULL AND nr_seq_segurado_conv_w::text <> '') then
			update 	pls_faturamento_conta
			set 	nr_seq_segurado_conv = nr_seq_segurado_conv_w 
			where 	nr_sequencia = r_c01_w.nr_sequencia;
		end if;
	
	end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
