CREATE OR REPLACE PROCEDURE pls_imp_pre_pagto_pck.incluir_inconsistencia ( nr_seq_conta_p pls_faturamento_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_fat_conta_proc.nr_sequencia%type, nr_seq_conta_mat_p pls_fat_conta_mat.nr_sequencia%type, cd_inconsistencia_p pls_inc_faturamento.cd_inconsistencia%type, nr_seq_inconsistencia_p pls_inc_faturamento.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_inconsistencia_w		pls_inc_faturamento.nr_sequencia%type;
	

BEGIN

	if (cd_inconsistencia_p IS NOT NULL AND cd_inconsistencia_p::text <> '') then
	
		select 	max(nr_sequencia)
		into STRICT	nr_seq_inconsistencia_w
		from	pls_inc_faturamento
		where 	cd_inconsistencia = cd_inconsistencia_p;
	
	else
		nr_seq_inconsistencia_w := nr_seq_inconsistencia_p;
	end if;

	insert into pls_faturamento_inc(	dt_atualizacao, dt_atualizacao_nrec, nm_usuario,
				nm_usuario_nrec, nr_seq_conta, nr_seq_conta_mat,
				nr_seq_conta_proc, nr_seq_inconsistencia, nr_sequencia)
	values (	clock_timestamp(), clock_timestamp(), nm_usuario_p,
			nm_usuario_p, nr_seq_conta_p, nr_seq_conta_mat_p,
			nr_seq_conta_proc_p, nr_seq_inconsistencia_w, nextval('pls_faturamento_inc_seq'));

END;

