-- Obter o conteudo da tag do XML



CREATE OR REPLACE FUNCTION pls_imp_pre_pagto_pck.obter_conteudo_tag ( ds_arquivo_xml_p ptu_aviso_arq_xml.ds_arquivo%type, ds_tag_p text, nr_tag_p bigint ) RETURNS text AS $body$
DECLARE


ds_retorno_w    text;
-- posicao pro substr

nr_substr_ini_w    bigint;
nr_substr_fim_w    bigint;
-- posicao pro instr

nr_instr_ini_w    bigint;
nr_instr_fim_w    bigint;
-- posicao

nr_pos_w    bigint := 1;
nr_tag_w    bigint := 1;
-- Tag

ds_tag_ini_w    varchar(255) := pls_imp_pre_pagto_pck.montar_tag('I',ds_tag_p);
ds_tag_fim_w    varchar(255) := pls_imp_pre_pagto_pck.montar_tag('F',ds_tag_p);


BEGIN
while(nr_tag_w <= nr_tag_p) loop
  nr_instr_ini_w  := instr(ds_arquivo_xml_p,ds_tag_ini_w,nr_pos_w);
  nr_instr_fim_w  := instr(ds_arquivo_xml_p,ds_tag_fim_w,nr_pos_w);

  nr_substr_ini_w := length(ds_tag_ini_w) + nr_instr_ini_w;
  nr_substr_fim_w := nr_instr_fim_w - (length(ds_tag_ini_w) + nr_instr_ini_w);

  ds_retorno_w  := substr(ds_arquivo_xml_p,nr_substr_ini_w,nr_substr_fim_w);
  nr_tag_w  := nr_tag_w + 1;
  nr_pos_w  := nr_instr_fim_w + length(ds_tag_fim_w);
end loop;

return ds_retorno_w;

END;

