CREATE OR REPLACE PROCEDURE pls_imp_xml_integracao_ws_pck.pls_imp_resp_solic_protocolo ( cd_usuario_plano_p pls_segurado_carteira.cd_usuario_plano%type, nr_seq_solicitacao_pa_p ptu_solicitacao_pa.nr_sequencia%type, cd_operadora_p ptu_resp_solicitacao_pa.cd_operadora%type, cd_operadora_destino_p ptu_resp_solicitacao_pa.cd_operadora_destino%type, cd_operadora_origem_p ptu_resp_solicitacao_pa.cd_operadora_origem%type, cd_transacao_p ptu_resp_solicitacao_pa.cd_transacao%type, ie_tipo_cliente_p ptu_resp_solicitacao_pa.ie_tipo_cliente%type, nr_registro_ans_p ptu_resp_solicitacao_pa.nr_registro_ans%type, ds_mensagem_livre_p ptu_resp_solicitacao_pa.ds_mensagem_livre%type, ie_origem_resposta_p ptu_resp_solicitacao_pa.ie_origem_resposta%type, ie_resposta_p ptu_resp_solicitacao_pa.ie_resposta%type, nr_seq_execucao_p ptu_resp_solicitacao_pa.nr_seq_execucao%type, nr_seq_protocolo_p ptu_resp_solicitacao_pa.nr_seq_protocolo%type, nr_versao_p ptu_resp_solicitacao_pa.nr_versao%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_resp_solic_pa_p INOUT ptu_resp_solicitacao_pa.nr_sequencia%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gravar nas tabelas de Resposta de Solicitacao de Protocolo do web service para 
		posteriormente serem lidas e a partir delas ser gerado o xml de resposta.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionario [ X] Tasy (Delphi/Java) [ ] Portal [  ]  Relatorios [ x] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao: 
		Transacao 002 - Resposta de Solicitacao de Protocolo
		ie_resposta_p: Pode vir com o valor 3(Finalizad0) ou nulo, quando vier nulo tratado para assumir o valor 1(Pendente).
		ie_resposta	 =>	1: Pendente
					2: Em andamento
					3: Finalizado
					4: Cancelado
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


nr_seq_segurado_w		pls_segurado_carteira.nr_seq_segurado%type;
nr_seq_protocolo_atend_w	pls_protocolo_atendimento.nr_sequencia%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
ie_tipo_cliente_w		ptu_resp_solicitacao_pa.ie_tipo_cliente%type;
ie_resposta_w			ptu_resp_solicitacao_pa.ie_resposta%type;

BEGIN

begin
	select	nr_seq_segurado
	into STRICT	nr_seq_segurado_w
	from	pls_segurado_carteira
	where	cd_usuario_plano = cd_usuario_plano_p;
exception
when others then
	nr_seq_segurado_w := null;
end;

if (coalesce(cd_estabelecimento_p,0) > 0) then
	cd_estabelecimento_w := cd_estabelecimento_p;
else
	cd_estabelecimento_w := pls_imp_xml_integracao_ws_pck.obter_estab_padrao_gpi();
end if;

ie_tipo_cliente_w := ptu_converter_tipo_cliente(ie_tipo_cliente_p);

if (ie_resposta_p IS NOT NULL AND ie_resposta_p::text <> '') then
	ie_resposta_w := ie_resposta_p;
else
	--se nao vier valor por padrao sera considerado que a solicitacao de protocolo que esta pendente

	ie_resposta_w := 1;
end if;

select	max(nr_seq_protocolo)
into STRICT	nr_seq_protocolo_atend_w
from	ptu_solicitacao_pa
where	nr_seq_execucao = nr_seq_execucao_p;

insert into ptu_resp_solicitacao_pa(nr_sequencia, cd_operadora, cd_operadora_destino,
	cd_operadora_origem, cd_transacao, ie_tipo_cliente,
	nr_registro_ans, cd_usuario_plano, ds_mensagem_livre,
	dt_geracao, ie_origem_resposta, ie_resposta,
	nr_protocolo, nr_seq_execucao, nr_seq_protocolo,
	dt_atualizacao, dt_atualizacao_nrec, nr_versao,
	nm_usuario, nm_usuario_nrec, cd_estabelecimento)
values (	nextval('ptu_resp_solicitacao_pa_seq'), cd_operadora_p, cd_operadora_destino_p,
	cd_operadora_origem_p, cd_transacao_p, ie_tipo_cliente_w,
	nr_registro_ans_p, cd_usuario_plano_p, ds_mensagem_livre_p,
	clock_timestamp(), ie_origem_resposta_p, ie_resposta_w,
	nr_seq_protocolo_p, nr_seq_execucao_p, nr_seq_protocolo_atend_w,
	clock_timestamp(), clock_timestamp(), nr_versao_p,
	nm_usuario_p, nm_usuario_p, cd_estabelecimento_w) returning nr_sequencia into nr_seq_resp_solic_pa_p;
commit;

END;

$body$
