	/*-----------------------------------------------------------------Indicadores - IDPR---------------------------------------------------------------------*/

CREATE OR REPLACE PROCEDURE pls_indicadores_idss_pck.pls_gerar_indicador_idpr ( cd_grupo_p text, cd_indicador_p text, dt_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
BEGIN

	if (cd_indicador_p = 'IDPR1') then -- Índice composto de Qualidade Cadastral
		CALL pls_indicadores_idss_pck.pls_obter_indice_quali_cadas( cd_grupo_p, cd_indicador_p, dt_referencia_p, cd_estabelecimento_p, nm_usuario_p);
	elsif (cd_indicador_p = 'IDPR2') then -- Taxa de utilização do SUS 
		CALL pls_indicadores_idss_pck.pls_obter_taxa_utiliza_sus( cd_grupo_p, cd_indicador_p, dt_referencia_p, cd_estabelecimento_p, nm_usuario_p);
	elsif (cd_indicador_p = 'IDPR3') then -- Razão de Completude do Envio dos Dados do Padrão TISS
		CALL pls_indicadores_idss_pck.pls_obter_completude_dados( cd_grupo_p, cd_indicador_p, dt_referencia_p, cd_estabelecimento_p, nm_usuario_p);
	elsif (cd_indicador_p = 'IDPR4') then -- Proporção de Glosas de Pagamentos a Prestadores de Serviços de Saúde
		CALL pls_indicadores_idss_pck.pls_obter_proporcao_glosas( cd_grupo_p, cd_indicador_p, dt_referencia_p, cd_estabelecimento_p, nm_usuario_p);
	elsif (cd_indicador_p = 'IDPR5') then -- Proporção de Diagnósticos Inespecíficos nos Eventos de Internação Preenchidos nas Guias TISS
		CALL pls_indicadores_idss_pck.pls_obter_diag_inespecifico( cd_grupo_p, cd_indicador_p, dt_referencia_p, cd_estabelecimento_p, nm_usuario_p);		
	elsif (coalesce(cd_indicador_p,'0') = '0') then -- Caso não selecionou nenhum Indicador
		CALL pls_indicadores_idss_pck.pls_gerar_indc_idpr_full( cd_grupo_p, cd_indicador_p, dt_referencia_p, cd_estabelecimento_p, nm_usuario_p);
	end if;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
