	/*-----------------------------------------------------------------Indicadores - IDSM---------------------------------------------------------------------*/

CREATE OR REPLACE PROCEDURE pls_indicadores_idss_pck.pls_gerar_indicador_idsm ( cd_grupo_p text, cd_indicador_p text, dt_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
BEGIN

	if (cd_indicador_p = 'IDSM1') then -- Índice de Recursos Próprios
		CALL pls_indicadores_idss_pck.pls_indice_recursos_proprios( cd_grupo_p, cd_indicador_p, dt_referencia_p, cd_estabelecimento_p, nm_usuario_p);
	elsif (cd_indicador_p = 'IDSM7') then -- Índice de Reajuste Médio Ponderado aplicado aos Planos Coletivos
		CALL pls_indicadores_idss_pck.pls_indice_reajuste_medio( cd_grupo_p, cd_indicador_p, dt_referencia_p, cd_estabelecimento_p, nm_usuario_p);
	elsif (coalesce(cd_indicador_p,'0') = '0') then -- Caso não selecionou nenhum Indicador
		CALL pls_indicadores_idss_pck.pls_gerar_indc_idsm_full( cd_grupo_p, cd_indicador_p, dt_referencia_p, cd_estabelecimento_p, nm_usuario_p);
	end if;

	END;

$body$
