SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_indicadores_idss_pck.pls_obter_completude_dados ( cd_grupo_p text, cd_indicador_p text, dt_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


	current_setting('pls_indicadores_idss_pck.cd_conta_contabil_w')::pls_indic_regra_item.cd_conta_contabil%type		pls_indic_regra_item.cd_conta_contabil%type;
	current_setting('pls_indicadores_idss_pck.ie_tipo_valor_contabil_w')::pls_indic_regra_item.ie_tipo_valor_contabil%type	pls_indic_regra_item.ie_tipo_valor_contabil%type;
	current_setting('pls_indicadores_idss_pck.nr_seq_regra_w')::pls_indic_regra_item.nr_seq_regra%type			pls_indic_regra.nr_sequencia%type;
	vl_contabil_w			ctb_movimento.vl_movimento%type;
	vl_monitoramento_w		pls_monitor_tiss_guia.vl_total_pago%type;

	c_seq_grupo_indc CURSOR FOR
		SELECT	a.nr_sequencia
		from	pls_indic_regra a
		where	dt_ref_inicio_mes_w between coalesce(pkg_date_utils.start_of(a.dt_inicio_vigencia,'MONTH'),dt_ref_inicio_mes_w) and coalesce(pkg_date_utils.start_of(a.dt_fim_vigencia,'MONTH'),dt_ref_inicio_mes_w)
		and	a.cd_grupo = cd_grupo_p
		and (a.cd_indicador = cd_indicador_p or coalesce(cd_indicador_p::text, '') = '');

	c_tipo_saldo CURSOR FOR
		SELECT	a.ie_tipo_valor_contabil,
			a.cd_conta_contabil
		from	pls_indic_regra_item a
		where	a.nr_seq_regra = nr_seq_grupo_indc_w;

	
BEGIN
	
	vl_contabil_w := 0;
	
	open c_seq_grupo_indc;
	loop
	fetch c_seq_grupo_indc into	
		current_setting('pls_indicadores_idss_pck.nr_seq_regra_w')::pls_indic_regra_item.nr_seq_regra%type;
	EXIT WHEN NOT FOUND; /* apply on c_seq_grupo_indc */
		begin
		
		open c_tipo_saldo;
		loop
		fetch c_tipo_saldo into	
			current_setting('pls_indicadores_idss_pck.ie_tipo_valor_contabil_w')::pls_indic_regra_item.ie_tipo_valor_contabil%type,
			current_setting('pls_indicadores_idss_pck.cd_conta_contabil_w')::pls_indic_regra_item.cd_conta_contabil%type;
		EXIT WHEN NOT FOUND; /* apply on c_tipo_saldo */
			begin
			vl_contabil_w := vl_contabil_w + pls_indicadores_idss_pck.pls_indic_soma_contabil(current_setting('pls_indicadores_idss_pck.ie_tipo_valor_contabil_w')::pls_indic_regra_item.ie_tipo_valor_contabil%type, current_setting('pls_indicadores_idss_pck.cd_conta_contabil_w')::pls_indic_regra_item.cd_conta_contabil%type, dt_referencia_p, cd_estabelecimento_p);
			end;
		end loop;
		close c_tipo_saldo;
		
		end;
	end loop;
	close c_seq_grupo_indc;
		
	select	sum(g.vl_total_pago)
	into STRICT	vl_monitoramento_w
	FROM pls_monitor_tiss_lote l, pls_monitor_tiss_guia g
LEFT OUTER JOIN pls_mon_tiss_guia_ret_act r ON (g.nr_sequencia = r.nr_seq_guia_monitor)
WHERE l.nr_sequencia = g.nr_seq_lote_monitor  and pkg_date_utils.start_of(l.dt_mes_competencia, 'MONTH') =  pkg_date_utils.start_of(dt_referencia_p, 'MONTH') and l.ie_tipo_lote = 1;
	
	PERFORM set_config('pls_indicadores_idss_pck.vl_total_w', dividir(vl_monitoramento_w, vl_contabil_w), false);
	
	insert into pls_indic_dados(	nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					cd_grupo,
					cd_indicador,
					dt_competencia,
					vl_indicador)
			values (nextval('pls_indic_dados_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					cd_grupo_p,
					cd_indicador_p,
					dt_referencia_p,
					current_setting('pls_indicadores_idss_pck.vl_total_w')::bigint);

	commit;

	PERFORM set_config('pls_indicadores_idss_pck.vl_div_w', 0, false);
	PERFORM set_config('pls_indicadores_idss_pck.vl_total_w', 0, false);

	END;	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_indicadores_idss_pck.pls_obter_completude_dados ( cd_grupo_p text, cd_indicador_p text, dt_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;
