CREATE OR REPLACE PROCEDURE pls_indicadores_idss_pck.pls_obter_ind_efetiva_comerc ( cd_grupo_p text, cd_indicador_p text, dt_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
BEGIN

	select	count(*)
	into STRICT	current_setting('pls_indicadores_idss_pck.nr_qt_1_w')::bigint
	from	pls_segurado a,
		pls_contrato b,
		pls_plano c
	where	b.nr_sequencia = a.nr_seq_contrato
	and	c.nr_sequencia = a.nr_seq_plano
	and	coalesce(a.nr_seq_titular::text, '') = ''
	and	c.ie_tipo_contratacao = 'I'
	and	pkg_date_utils.start_of(b.dt_contrato,'YEAR') = pkg_date_utils.start_of(dt_referencia_p,'YEAR');

	select	count(*)
	into STRICT	current_setting('pls_indicadores_idss_pck.nr_qt_2_w')::bigint
	from	pls_segurado a,
		pls_contrato b,
		pls_plano c
	where	b.nr_sequencia = a.nr_seq_contrato
	and	c.nr_sequencia = a.nr_seq_plano
	and	coalesce(a.nr_seq_titular::text, '') = ''
	and	c.ie_tipo_contratacao = 'I'
	and	pkg_date_utils.start_of(b.dt_contrato,'YEAR') = pkg_date_utils.start_of(pkg_date_utils.add_month(dt_referencia_p, -12),'YEAR');

	select	count(*)
	into STRICT	current_setting('pls_indicadores_idss_pck.nr_qt_3_w')::bigint
	from	pls_segurado a,
		pls_contrato b,
		pls_plano c
	where	b.nr_sequencia = a.nr_seq_contrato
	and	c.nr_sequencia = a.nr_seq_plano
	and	coalesce(a.nr_seq_titular::text, '') = ''
	and	pkg_date_utils.start_of(b.dt_contrato,'YEAR') = pkg_date_utils.start_of(pkg_date_utils.add_month(dt_referencia_p, -12),'YEAR');

	PERFORM set_config('pls_indicadores_idss_pck.vl_total_w', dividir((current_setting('pls_indicadores_idss_pck.nr_qt_1_w')::bigint - current_setting('pls_indicadores_idss_pck.nr_qt_2_w')::bigint), current_setting('pls_indicadores_idss_pck.nr_qt_3_w')::bigint), false);

	insert into pls_indic_dados(	nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					cd_grupo,
					cd_indicador,
					dt_competencia,
					vl_indicador)
			values (nextval('pls_indic_dados_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					cd_grupo_p,
					cd_indicador_p,
					dt_referencia_p,
					current_setting('pls_indicadores_idss_pck.vl_total_w')::bigint);

	commit;

	PERFORM set_config('pls_indicadores_idss_pck.vl_total_w', 0, false);

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
