CREATE OR REPLACE PROCEDURE pls_indicadores_idss_pck.pls_obter_indice_quali_cadas ( cd_grupo_p text, cd_indicador_p text, dt_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
BEGIN

	select	count(p.ie_situacao)
	into STRICT	current_setting('pls_indicadores_idss_pck.vl_numerador_w')::pls_conta_proc.vl_liberado%type
	from	pls_segurado s,
		pls_plano p,
		pessoa_fisica f
	where	p.nr_sequencia = s.nr_seq_plano
	and	f.cd_pessoa_fisica = s.cd_pessoa_fisica
	and	(f.nr_cartao_nac_sus IS NOT NULL AND f.nr_cartao_nac_sus::text <> '')
	and	pls_obter_dados_segurado(s.nr_sequencia, 'CS') = 'A'
	and	((obter_se_cpf_valido(f.nr_cpf) = 'S' and pls_obter_dados_segurado(s.nr_sequencia, 'ID') >= 18)
	or (pls_obter_dados_segurado(s.nr_sequencia, 'ID') <= 17))
	and	coalesce(s.dt_rescisao, dt_referencia_p) >= dt_referencia_p
	and	(s.dt_liberacao IS NOT NULL AND s.dt_liberacao::text <> '')
	and	s.dt_contratacao <= dt_referencia_p;

	select	count(*)
	into STRICT	current_setting('pls_indicadores_idss_pck.vl_denominador_w')::pls_conta_proc.vl_liberado%type
	from	pls_outorgante o,
		pls_plano p,
		pls_segurado s
	where	o.nr_sequencia = p.nr_seq_outorgante
	and	p.nr_sequencia = s.nr_seq_plano
	and	pls_obter_dados_segurado(s.nr_sequencia, 'CS') = 'A'
	and	o.cd_estabelecimento = cd_estabelecimento_p
	and	coalesce(s.dt_rescisao, dt_referencia_p) >= dt_referencia_p
	and	(s.dt_liberacao IS NOT NULL AND s.dt_liberacao::text <> '')
	and	s.dt_contratacao <= dt_referencia_p;

	PERFORM set_config('pls_indicadores_idss_pck.vl_total_w', dividir(current_setting('pls_indicadores_idss_pck.vl_numerador_w')::pls_conta_proc.vl_liberado%type, current_setting('pls_indicadores_idss_pck.vl_denominador_w')::pls_conta_proc.vl_liberado%type) * 100, false);

	insert into pls_indic_dados(	nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					cd_grupo,
					cd_indicador,
					dt_competencia,
					vl_indicador)
			values (nextval('pls_indic_dados_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					cd_grupo_p,
					cd_indicador_p,
					dt_referencia_p,
					current_setting('pls_indicadores_idss_pck.vl_total_w')::bigint);

	commit;

	PERFORM set_config('pls_indicadores_idss_pck.vl_div_w', 0, false);
	PERFORM set_config('pls_indicadores_idss_pck.vl_total_w', 0, false);

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_indicadores_idss_pck.pls_obter_indice_quali_cadas ( cd_grupo_p text, cd_indicador_p text, dt_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;
