CREATE OR REPLACE PROCEDURE pls_int_xml_cta_pck.atualiza_pls_protocolo_conta ( tb_nr_seq_protocolo_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_prestador_conv_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_periodo_p INOUT pls_util_cta_pck.t_number_table, tb_qt_contas_protocolo_p INOUT pls_util_cta_pck.t_number_table, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN
if (tb_nr_seq_protocolo_p.count > 0) then
	forall i in tb_nr_seq_protocolo_p.first..tb_nr_seq_protocolo_p.last
		update	pls_protocolo_conta
		set	ie_situacao		= 'T',
			dt_integracao		= clock_timestamp(),
			nm_usuario_integracao	= nm_usuario_p,
			cd_condicao_pagamento	= pls_obter_dados_prestador(tb_nr_seq_prestador_conv_p(i),'CP'),
			dt_base_venc		= clock_timestamp(),
			nr_seq_periodo_pgto	= tb_nr_seq_periodo_p(i),
			qt_contas_informadas    = coalesce(tb_qt_contas_protocolo_p(i),0),
			dt_recebimento		= coalesce(dt_recebimento,pls_int_xml_cta_pck.obter_se_atualiza_dt_receb(nr_sequencia, dt_recebimento, nm_usuario))
		where	nr_sequencia		= tb_nr_seq_protocolo_p(i)
		and	cd_estabelecimento	= cd_estabelecimento_p;
	commit;
end if;

tb_nr_seq_protocolo_p.delete;
tb_nr_seq_prestador_conv_p.delete;
tb_nr_seq_periodo_p.delete;
tb_qt_contas_protocolo_p.delete;
	
END;

$body$
