SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_int_xml_cta_pck.gerar_analise_lote_protocolo ( nr_seq_lote_protocolo_p pls_lote_protocolo_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


ie_tipo_importacao_w		pls_protocolo_conta.ie_tipo_importacao%type;
qt_prestador_regra_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_seq_prestador
	from	pls_protocolo_conta a
	where	a.nr_seq_lote_conta 	= nr_seq_lote_protocolo_p
	and	ie_status		<> '4';

BEGIN
if (current_setting('pls_int_xml_cta_pck.ie_analise_conta_w')::pls_param_importacao_conta.ie_analise_conta%type = 'S') and (coalesce(current_setting('pls_int_xml_cta_pck.qt_regra_lib_analise_w')::bigint,0) > 0) then
	for r_C01_w in C01 loop
		begin
		qt_prestador_regra_w := pls_int_xml_cta_pck.verifica_regra_lib_analise(r_C01_w.nr_seq_prestador);
		if (qt_prestador_regra_w > 0) then
			exit;
		end if;
		end;
	end loop;
elsif (current_setting('pls_int_xml_cta_pck.ie_analise_conta_w')::pls_param_importacao_conta.ie_analise_conta%type = 'S') and (coalesce(current_setting('pls_int_xml_cta_pck.qt_regra_lib_analise_w')::bigint,0) = 0) then
	qt_prestador_regra_w := 1;
end if;

select	coalesce(max(ie_tipo_importacao), 'UP')
into STRICT	ie_tipo_importacao_w
from	pls_protocolo_conta a
where	a.nr_seq_lote_conta	 = nr_seq_lote_protocolo_p;

--Sempre realizar_ a an_lise do lote se estiver importando pelo WebService

if (current_setting('pls_int_xml_cta_pck.ie_analise_conta_w')::pls_param_importacao_conta.ie_analise_conta%type = 'N') or (nm_usuario_p = 'WebService') or (coalesce(qt_prestador_regra_w,0) = 0)then
	CALL pls_gerar_analise_lote(nr_seq_lote_protocolo_p, 'N', 'N', cd_estabelecimento_p, nm_usuario_p, null, 'S', ie_tipo_importacao_w);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
