CREATE OR REPLACE PROCEDURE pls_int_xml_cta_pck.gerar_resumo_conta ( nr_seq_lote_protocolo_p pls_lote_protocolo_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


qt_prestador_regra_w	pls_prestador.nr_sequencia%type;
				
C01 CURSOR(nr_seq_lote_protocolo_pc pls_lote_protocolo_conta.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_prestador
	from	pls_protocolo_conta b,
		pls_conta a
	where	b.nr_seq_lote_conta	= nr_seq_lote_protocolo_pc
	and	b.ie_situacao		= 'A'
	and	a.nr_seq_protocolo	= b.nr_sequencia;
	
BEGIN

for r_c01_w in C01(nr_seq_lote_protocolo_p) loop
	if (coalesce(current_setting('pls_int_xml_cta_pck.qt_regra_lib_analise_w')::bigint,0) > 0) then
		qt_prestador_regra_w := pls_int_xml_cta_pck.verifica_regra_lib_analise(r_C01_w.nr_seq_prestador);
	else
		qt_prestador_regra_w := 1;
	end if;
	
	if (qt_prestador_regra_w > 0) then
		CALL pls_cta_consistir_pck.gerar_resumo_conta(null, null, null, r_c01_w.nr_sequencia, nm_usuario_p, cd_estabelecimento_p);
	end if;
end loop;

commit;	

END;

$body$
