CREATE OR REPLACE PROCEDURE pls_int_xml_cta_pck.pls_atualiza_lote_prot_aceito ( nr_seq_protocolo_p pls_protocolo_conta_imp.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

ie_pode_integrar_lote_prot_w	varchar(1);
nr_seq_lote_protocolo_w		pls_lote_protocolo_conta.nr_sequencia%type;

BEGIN

select	max(nr_seq_lote_protocolo)
into STRICT	nr_seq_lote_protocolo_w
from	pls_protocolo_conta_imp
where	nr_sequencia	= nr_seq_protocolo_p;

-- verifica se o lote do protocolo pode ser integrado

ie_pode_integrar_lote_prot_w := pls_int_xml_cta_pck.verifica_pode_integrar_lote(nr_seq_lote_protocolo_w);

--Atualiza a situa__o do protocolo para ACEITO

--Ap_s verificar que n_o h_ ocorr_ncias impeditivas

if (ie_pode_integrar_lote_prot_w = 'S') then
	update	pls_protocolo_conta_imp
	set	ie_situacao	= 'A',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_protocolo_p;
	
	commit;
else
	update	pls_protocolo_conta_imp
	set	ie_situacao	= 'RE',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_protocolo_p;
	
	commit;
end if;	

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
