			tb_cd_uf_cons_prof_conv_w(i), tb_cd_uf_conselho_prof_w(i), 
			tb_nr_seq_prestador_w(i));
		
		/*Verificar a rotina pls_gerar_proc_participante
		
		nr_seq_prestador*/

	commit;
end loop;
