CREATE OR REPLACE PROCEDURE pls_int_xml_cta_pck.verifica_ocor_ativa_prot ( nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

--Ap_s processar gera__o de contas  no caso da nova importa__o do XML as convers_es nevess_rias, atualiza situa__o do prototocolo para rejeitado no caso de existir alguma ocorr_ncia ativa 

--Apenas  se for para usar a nova forma de importa__o XML. Obs: Basta verificar ocorr_ncia por sequ_ncia das contas, mesmo que for ocorr_ncia a n_vel de _tem ir_ cair no select

qt_ocor_ativas_w	integer;


BEGIN

select 	count(1)
into STRICT	qt_ocor_ativas_w
from	pls_ocorrencia_imp
where	nr_seq_conta in ( 	SELECT 	nr_sequencia
				from	pls_conta_imp
				where 	nr_seq_protocolo = nr_seq_protocolo_p)
and ie_situacao = 'A';

if ( qt_ocor_ativas_w > 0) then

	update 	pls_protocolo_conta_imp
	set	ie_situacao	= 'RE',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()	
	where	nr_sequencia	= nr_seq_protocolo_p;

end if;

END;					

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
