CREATE OR REPLACE FUNCTION pls_int_xml_cta_pck.verifica_pode_integrar_lote ( nr_seq_lote_protocolo_p pls_lote_protocolo_conta.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_pode_integrar_lote_prot_w	varchar(1);
qt_registro_w			bigint;


BEGIN

ie_pode_integrar_lote_prot_w := 'N';

-- Verifica se existe alguma ocorr_ncia impeditiva de integra__o nos Materiais, Procedimentos e Contas.

-- Se houver, n_o permite a integra__o das informa__es at_ que sejam ajustadas as ocorr_ncias.

select	sum(qt_registro)
into STRICT	qt_registro_w
from (
	SELECT	count(1) qt_registro
	from	pls_protocolo_conta_imp e,
		pls_conta_imp d,
		pls_conta_mat_imp f,
		pls_ocorrencia_imp a,
		pls_ocorrencia b,
		tiss_motivo_glosa c
	where	e.nr_seq_lote_protocolo	= nr_seq_lote_protocolo_p
	and	d.nr_seq_protocolo	= e.nr_sequencia
	and	d.nr_sequencia		= f.nr_seq_conta
	and	a.nr_seq_conta_mat	= f.nr_sequencia
	and	d.nr_sequencia		= a.nr_seq_conta
	and	a.nr_seq_ocorrencia	= b.nr_sequencia
	and	b.ie_situacao		= 'A'
	and	b.nr_seq_motivo_glosa	= c.nr_sequencia
	
union	all

	PERFORM	count(1) qt_registro
	from	pls_protocolo_conta_imp e,
		pls_conta_imp d,
		pls_conta_mat_imp f,
		pls_ocorrencia_imp a,
		pls_ocorrencia b,
		tiss_motivo_glosa c
	where	e.nr_seq_lote_protocolo	= nr_seq_lote_protocolo_p
	and	d.nr_seq_protocolo	= e.nr_sequencia
	and	d.nr_sequencia		= f.nr_seq_conta
	and	a.nr_seq_conta_mat	= f.nr_sequencia
	and	d.nr_sequencia		= a.nr_seq_conta
	and	a.nr_seq_ocorrencia	= b.nr_sequencia
	and	b.ie_situacao		= 'A'
	and	b.nr_seq_motivo_glosa	= c.nr_sequencia
	and	c.ie_permite_alteracao_ops = 'N'
	
union all

	select	count(1) qt_registro
	from	pls_protocolo_conta_imp e,
		pls_conta_imp d,
		pls_conta_proc_imp f,
		pls_ocorrencia_imp a,
		pls_ocorrencia b,
		tiss_motivo_glosa c
	where	e.nr_seq_lote_protocolo	= nr_seq_lote_protocolo_p
	and	d.nr_seq_protocolo	= e.nr_sequencia
	and	d.nr_sequencia		= f.nr_seq_conta
	and	a.nr_seq_conta_proc	= f.nr_sequencia
	and	d.nr_sequencia		= a.nr_seq_conta
	and	a.nr_seq_ocorrencia	= b.nr_sequencia
	and	b.ie_situacao		= 'A'
	and	b.nr_seq_motivo_glosa	= c.nr_sequencia
	
union	all

	select	count(1) qt_registro
	from	pls_protocolo_conta_imp e,
		pls_conta_imp d,
		pls_conta_proc_imp f,
		pls_ocorrencia_imp a,
		pls_ocorrencia b,
		tiss_motivo_glosa c
	where	e.nr_seq_lote_protocolo	= nr_seq_lote_protocolo_p
	and	d.nr_seq_protocolo	= e.nr_sequencia
	and	d.nr_sequencia		= f.nr_seq_conta
	and	a.nr_seq_conta_proc	= f.nr_sequencia
	and	d.nr_sequencia		= a.nr_seq_conta
	and	a.nr_seq_ocorrencia	= b.nr_sequencia
	and	b.ie_situacao		= 'A'
	and	b.nr_seq_motivo_glosa	= c.nr_sequencia
	and	c.ie_permite_alteracao_ops = 'N'
	
union	all

	select	count(1) qt_registro
	from	pls_protocolo_conta_imp e,
		pls_conta_imp d,
		pls_ocorrencia_imp a,
		pls_ocorrencia b,
		tiss_motivo_glosa c
	where	e.nr_seq_lote_protocolo	= nr_seq_lote_protocolo_p
	and	d.nr_seq_protocolo	= e.nr_sequencia
	and	d.nr_sequencia		= a.nr_seq_conta
	and	a.nr_seq_ocorrencia	= b.nr_sequencia
	and	b.ie_situacao		= 'A'
	and	b.nr_seq_motivo_glosa	= c.nr_sequencia
	
union	all

	select	count(1) qt_registro
	from	pls_protocolo_conta_imp e,
		pls_conta_imp d,
		pls_ocorrencia_imp a,
		pls_ocorrencia b,
		tiss_motivo_glosa c
	where	e.nr_seq_lote_protocolo	= nr_seq_lote_protocolo_p
	and	d.nr_seq_protocolo	= e.nr_sequencia
	and	d.nr_sequencia		= a.nr_seq_conta
	and	a.nr_seq_ocorrencia	= b.nr_sequencia
	and	b.ie_situacao		= 'A'
	and	b.nr_seq_motivo_glosa	= c.nr_sequencia
	and	c.ie_permite_alteracao_ops = 'N') alias7;

if (qt_registro_w > 0) then
	ie_pode_integrar_lote_prot_w	:= 'N';
else
	ie_pode_integrar_lote_prot_w	:= 'S';
end if;

return ie_pode_integrar_lote_prot_w;

END;

$body$
