CREATE OR REPLACE PROCEDURE pls_mens_atualizacao_pck.atualizar_valores_lote ( nr_seq_lote_p pls_lote_mensalidade.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, ie_tipo_atualizacao_p text default null) AS $body$
DECLARE

ie_tipo_atualizacao_w	varchar(2);

BEGIN

ie_tipo_atualizacao_w	:= coalesce(ie_tipo_atualizacao_p, 'T');

if (ie_tipo_atualizacao_w = 'T') then
	-- Carregar as tabelas temporarias

	CALL pls_mens_atualizacao_pck.carregar_tabelas_tmp(nr_seq_lote_p, nm_usuario_p);
end if;

--Atualizar valores das mensalidades

CALL pls_mens_atualizacao_pck.atualizar_mensalidade_segurado();
CALL pls_mens_atualizacao_pck.atualizar_mensalidade();

if (ie_tipo_atualizacao_w = 'T') then
	--Deletar as mensalidades com valor negativo e gerar log de erro

	CALL pls_mens_atualizacao_pck.delete_mens_vl_negativo(nr_seq_lote_p, nm_usuario_p, cd_estabelecimento_p);
end if;

--Atualizar valor do lote

CALL pls_mens_atualizacao_pck.atualizar_lote(nr_seq_lote_p);

end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
