CREATE OR REPLACE PROCEDURE pls_mens_atualizacao_pck.carregar_tabelas_tmp ( nr_seq_lote_p pls_lote_mensalidade.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE

nr_seq_mens_seg_t	pls_util_cta_pck.t_number_table;
