C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_mensalidade_segurado
	where	nr_seq_lote	= nr_seq_lote_p;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_mensalidade
