	exit when nr_seq_mens_seg_t.count = 0;
	
	forall i in nr_seq_mens_seg_t.first..nr_seq_mens_seg_t.last
		insert into pls_mensalidade_seg_tmp
		values (	nr_seq_mens_seg_t(i));
	commit;
	
end loop;
