CREATE OR REPLACE PROCEDURE pls_mens_atualizacao_pck.delete_mens_vl_negativo ( nr_seq_lote_p pls_lote_mensalidade.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


ie_gerou_log_benef_w	varchar(1);
					
C01 CURSOR FOR
	SELECT	nr_sequencia nr_seq_mensalidade,
		nr_seq_pagador,
		vl_mensalidade
	from	pls_mensalidade
	where	nr_seq_lote	= nr_seq_lote_p
	and	vl_mensalidade	< 0;
	
c02 CURSOR(	nr_seq_mensalidade_pc	pls_mensalidade.nr_sequencia%type) FOR
	SELECT	nr_seq_segurado,
		vl_mensalidade
	from	pls_mensalidade_segurado
	where	vl_mensalidade < 0
	and	nr_seq_mensalidade = nr_seq_mensalidade_pc;
	
BEGIN

for r_c01_w in C01 loop
	begin
	ie_gerou_log_benef_w	:= 'N';
	
	for r_c02_w in c02(r_c01_w.nr_seq_mensalidade) loop
		begin
		CALL pls_gerar_mens_log_erro(nr_seq_lote_p,r_c01_w.nr_seq_pagador,r_c02_w.nr_seq_segurado,null, 'Mensalidade com valor negativo: R$ '||campo_mascara_virgula(r_c02_w.vl_mensalidade),cd_estabelecimento_p,nm_usuario_p);
		ie_gerou_log_benef_w	:= 'S';
		end;
	end loop;
	
	CALL pls_deletar_mensalidade(r_c01_w.nr_seq_mensalidade, 'S', nm_usuario_p, 'S');
	
	if (ie_gerou_log_benef_w = 'N') then
		CALL pls_gerar_mens_log_erro(nr_seq_lote_p,r_c01_w.nr_seq_pagador,null,null, 'Mensalidade com valor negativo: R$ '||campo_mascara_virgula(r_c01_w.vl_mensalidade),cd_estabelecimento_p,nm_usuario_p);
	end if;
	end;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
