CREATE OR REPLACE PROCEDURE pls_mens_criticas_pck.critica_bonificacao_duplicada ( nr_seq_mensalidade_seg_p pls_mensalidade_segurado.nr_sequencia%type) AS $body$
DECLARE


C01 CURSOR(	nr_seq_mensalidade_seg_pc	pls_mensalidade_segurado.nr_sequencia%type) FOR
SELECT distinct a.nr_sequencia nr_seq_mensalidade_seg
from		pls_mensalidade_segurado a,
		pls_mensalidade_seg_item b,
		pls_bonificacao_vinculo c
where		b.nr_seq_mensalidade_seg = a.nr_sequencia
and		c.nr_sequencia = b.nr_seq_bonificacao_vinculo
and		a.nr_sequencia = nr_seq_mensalidade_seg_pc
group by	a.nr_sequencia, c.nr_seq_bonificacao
having 		count(1) > 1;

BEGIN

for r_c01_w in C01(nr_seq_mensalidade_seg_p) loop
	CALL pls_mens_criticas_pck.add_critica(r_c01_w.nr_seq_mensalidade_seg, 13);
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
