CREATE OR REPLACE PROCEDURE pls_mens_criticas_pck.critica_fechamento_conta ( nr_seq_mensalidade_seg_p pls_mensalidade_segurado.nr_sequencia%type, dt_referencia_p pls_mensalidade_segurado.dt_mesano_referencia%type) AS $body$
DECLARE


ie_gerar_critica_w	varchar(1);
qt_baixa_conta_w	bigint;		
		
C01 CURSOR(	nr_seq_mensalidade_seg_pc	pls_mensalidade_segurado.nr_sequencia%type,
		dt_referencia_pc		pls_mensalidade_segurado.dt_mesano_referencia%type) FOR
	SELECT	obter_dias_entre(coalesce(b.dt_fechamento_conta, dt_referencia_pc), dt_referencia_pc) qt_dias,
		b.nr_sequencia nr_seq_conta
	from	pls_mensalidade_seg_item a,
		pls_conta b
	where	b.nr_sequencia = a.nr_seq_conta
	and	a.nr_seq_mensalidade_seg = nr_seq_mensalidade_seg_pc
	and	a.ie_tipo_item in ('3','6','13');

BEGIN

ie_gerar_critica_w	:= 'N';
qt_baixa_conta_w	:= 0;

for r_c01_w in c01(	nr_seq_mensalidade_seg_p,
			dt_referencia_p) loop
	begin
	if (ie_gerar_critica_w = 'N') then
		if (r_c01_w.qt_dias > 90) then
			select	count(1)
			into STRICT	qt_baixa_conta_w
			from	pls_mensalidade_seg_item b,
				pls_mensalidade_segurado c,
				pls_mensalidade d
			where	d.nr_sequencia = c.nr_seq_mensalidade
			and	c.nr_sequencia = b.nr_seq_mensalidade_seg
			and	coalesce(d.ie_cancelamento::text, '') = ''
			and	b.nr_seq_conta = r_c01_w.nr_seq_conta
			and	c.nr_sequencia <> nr_seq_mensalidade_seg_p;
			
			if (qt_baixa_conta_w = 0) then
				ie_gerar_critica_w	:= 'S';
			end if;
		end if;
	end if;
	end;
end loop;

if (ie_gerar_critica_w = 'S') then
	CALL pls_mens_criticas_pck.add_critica(nr_seq_mensalidade_seg_p, 9);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
