SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_mens_criticas_pck.criticar_apropriacao ( nr_seq_mensalidade_seg_p pls_mensalidade_segurado.nr_sequencia%type) AS $body$
DECLARE


vl_apropriacao_item_w	pls_mens_seg_item_aprop.vl_apropriacao%type;
ie_inconsistente_w	varchar(1);

C01 CURSOR(	nr_seq_mensalidade_seg_pc	pls_mensalidade_segurado.nr_sequencia%type) FOR
	SELECT	nr_sequencia nr_seq_item,
		(vl_item - vl_sca_embutido) vl_item
	from	pls_mensalidade_seg_item
	where	nr_seq_mensalidade_seg	= nr_seq_mensalidade_seg_pc
	and	ie_valor_apropriado = 'S';

C02 CURSOR(nr_seq_item_pc	pls_mensalidade_seg_item.nr_sequencia%type) FOR
	SELECT	vl_apropriacao
	from	pls_mens_seg_item_aprop
	where	nr_seq_item	= nr_seq_item_pc;

BEGIN

for r_c01_w in C01(nr_seq_mensalidade_seg_p) loop
	begin
	vl_apropriacao_item_w	:= 0;
	ie_inconsistente_w	:= 'N';
	
	for r_c02_w in C02(r_c01_w.nr_seq_item) loop
		begin
		vl_apropriacao_item_w	:= vl_apropriacao_item_w + r_c02_w.vl_apropriacao;
		
		if (r_c01_w.vl_item > 0) and (r_c02_w.vl_apropriacao < 0) then
			ie_inconsistente_w	:= 'S';
		elsif (r_c01_w.vl_item < 0) and (r_c02_w.vl_apropriacao > 0) then
			ie_inconsistente_w	:= 'S';
		end if;
		end;
	end loop; --C02
	
	if (vl_apropriacao_item_w <> r_c01_w.vl_item) or (ie_inconsistente_w = 'S') then
		CALL pls_mens_criticas_pck.add_critica(nr_seq_mensalidade_seg_p, 4);
	end if;
	end;
end loop; --C01

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
