CREATE OR REPLACE PROCEDURE pls_mens_itens_conta_pck.carregar_regra_grupo ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_seq_intercambio_p pls_intercambio.nr_sequencia%type) AS $body$
BEGIN
if (pls_mensalidade_util_pck.get_seq_regra_grupo IS NOT NULL AND pls_mensalidade_util_pck.get_seq_regra_grupo::text <> '') then
	select	max(a.nr_seq_prestador),
		max(a.nr_seq_prestador_atend),
		max(a.ie_tipo_prestador_exec),
		max(a.ie_tipo_prestador_atend),
		max(a.ie_tipo_guia),
		max(a.ie_tipo_segurado),
		max(a.ie_preco)
	into STRICT	current_setting('pls_mens_itens_conta_pck.nr_seq_prestador_regra_exec_w')::pls_mensalidade_grupo.nr_seq_prestador%type,
		current_setting('pls_mens_itens_conta_pck.nr_seq_prestador_regra_atend_w')::pls_mensalidade_grupo.nr_seq_prestador_atend%type,
		current_setting('pls_mens_itens_conta_pck.ie_tipo_prestador_exec_regra_w')::pls_mensalidade_grupo.ie_tipo_prestador_exec%type,
		current_setting('pls_mens_itens_conta_pck.ie_tipo_prestador_atend_regr_w')::pls_mensalidade_grupo.ie_tipo_prestador_atend%type,
		current_setting('pls_mens_itens_conta_pck.ie_tipo_guia_regra_w')::pls_mensalidade_grupo.ie_tipo_guia%type,
		current_setting('pls_mens_itens_conta_pck.ie_tipo_segurado_regra_w')::pls_mensalidade_grupo.ie_tipo_segurado%type,
		current_setting('pls_mens_itens_conta_pck.ie_preco_regra_w')::pls_mensalidade_grupo.ie_preco%type
	from	pls_mensalidade_grupo	a,
		pls_mens_grupo_contrato	b
	where	a.nr_sequencia	= b.nr_seq_grupo
	and	a.nr_sequencia	= pls_mensalidade_util_pck.get_seq_regra_grupo
	and	((((b.nr_seq_contrato = nr_seq_contrato_p) or (coalesce(nr_seq_contrato_p::text, '') = ''))
	and	((b.nr_seq_contrato_inter = nr_seq_intercambio_p) or (coalesce(nr_seq_intercambio_p::text, '') = ''))) or
		(exists (	SELECT 1
				from	pls_preco_contrato t
				where	t.nr_seq_grupo	= b.nr_seq_grupo_contrato
				and	((t.nr_seq_contrato = nr_seq_contrato_p) or (t.nr_seq_intercambio = nr_seq_intercambio_p)))));
else
	PERFORM set_config('pls_mens_itens_conta_pck.nr_seq_prestador_regra_exec_w', null, false);
	PERFORM set_config('pls_mens_itens_conta_pck.nr_seq_prestador_regra_atend_w', null, false);
	PERFORM set_config('pls_mens_itens_conta_pck.ie_tipo_prestador_exec_regra_w', null, false);
	PERFORM set_config('pls_mens_itens_conta_pck.ie_tipo_prestador_atend_regr_w', null, false);
	PERFORM set_config('pls_mens_itens_conta_pck.ie_tipo_guia_regra_w', null, false);
	PERFORM set_config('pls_mens_itens_conta_pck.ie_tipo_segurado_regra_w', null, false);
	PERFORM set_config('pls_mens_itens_conta_pck.ie_preco_regra_w', null, false);
end if;
END;

$body$
