CREATE OR REPLACE PROCEDURE pls_mens_itens_conta_pck.gerar_taxa_atendimento ( nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

C01 CURSOR(nr_seq_segurado_pc	pls_segurado.nr_sequencia%type) FOR
	SELECT	nr_sequencia,
		nr_seq_conta,
		nr_seq_protocolo,
		dt_competencia_mens,
		vl_liberado
	from	pls_conta_val_atend
	where	nr_seq_segurado 	= nr_seq_segurado_pc
	and	coalesce(nr_seq_mensalidade_seg::text, '') = ''
	and	ie_cobrar_mensalidade	= 'S';
BEGIN

for r_c01_w in C01(current_setting('pls_mens_itens_conta_pck.pls_segurado_w')::pls_segurado%rowtype.nr_sequencia) loop
	begin
	if	((coalesce(r_c01_w.dt_competencia_mens::text, '') = '') or (r_c01_w.dt_competencia_mens = current_setting('pls_mens_itens_conta_pck.dt_referencia_w')::timestamp)) then
		current_setting('pls_mens_itens_conta_pck.ie_inseriu_item_w')::varchar(1) := pls_mens_itens_pck.add_item_conta(current_setting('pls_mens_itens_conta_pck.nr_seq_mensalidade_seg_w')::pls_mensalidade_segurado.nr_sequencia%type, '38', r_c01_w.vl_liberado, r_c01_w.nr_seq_conta, r_c01_w.nr_seq_protocolo, current_setting('pls_mens_itens_conta_pck.pls_segurado_w')::pls_segurado%rowtype.nr_sequencia, current_setting('pls_mens_itens_conta_pck.pls_segurado_w')::pls_segurado%rowtype.nr_seq_titular, 0, 0, 0, wheb_mensagem_pck.get_texto(334443,null), null, current_setting('pls_mens_itens_conta_pck.ie_inseriu_item_w')::varchar(1));
		
		if (current_setting('pls_mens_itens_conta_pck.ie_inseriu_item_w')::varchar(1) = 'S') then
			update	pls_conta_val_atend
			set	nr_seq_mensalidade_seg = current_setting('pls_mens_itens_conta_pck.nr_seq_mensalidade_seg_w')::pls_mensalidade_segurado.nr_sequencia%type
			where	nr_sequencia = r_c01_w.nr_sequencia;
		end if;
	end if;
	end;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
