CREATE OR REPLACE FUNCTION pls_mens_itens_conta_pck.obter_observacao_item_conta ( ie_acao_p text, nr_seq_regra_p bigint, vl_limite_p bigint, vl_saldo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_observacao_w			pls_mensalidade_item_conta.ds_observacao%type;
			

BEGIN

if (ie_acao_p = 1) then
	ds_observacao_w	:= wheb_mensagem_pck.get_texto(1127945,	'NR_SEQ_REGRA='||nr_seq_regra_p||';VL_LIMITE='||campo_mascara_virgula(vl_limite_p));
elsif (ie_acao_p = 2) then
	ds_observacao_w	:= wheb_mensagem_pck.get_texto(1127946,	'NR_SEQ_REGRA='||nr_seq_regra_p||';VL_LIMITE='||campo_mascara_virgula(vl_limite_p)||';VL_SALDO_ITEM='||campo_mascara_virgula(vl_saldo_p));
else
	ds_observacao_w	:= null;
end if;

return	ds_observacao_w;

END;

