CREATE OR REPLACE PROCEDURE pls_mens_itens_conta_pck.tratar_valores_ato_cooperado ( vl_item_p bigint, vl_saldo_p bigint, vl_baixa_ato_coop_p bigint, vl_baixa_ato_nao_coop_p bigint, vl_baixa_ato_auxiliar_p bigint, ie_encerrou_saldo_item_p text, vl_ato_cooperado_p INOUT bigint, vl_ato_nao_cooperado_p INOUT bigint, vl_ato_auxiliar_p INOUT bigint) AS $body$
DECLARE


vl_ato_cooperado_w		double precision;
vl_ato_auxiliar_w		double precision;
vl_ato_nao_cooperado_w		double precision;
vl_saldo_w			double precision;
	

BEGIN

vl_ato_cooperado_w		:= vl_ato_cooperado_p;
vl_ato_nao_cooperado_w		:= vl_ato_nao_cooperado_p;
vl_ato_auxiliar_w		:= vl_ato_auxiliar_p;

if (vl_saldo_p = 0) then
	vl_saldo_w	:= vl_item_p;
else
	vl_saldo_w	:= vl_saldo_p;
end if;

if (vl_ato_cooperado_w > 0) then
	vl_ato_cooperado_w	:= (vl_ato_cooperado_w / vl_item_p) * vl_saldo_w;
end if;

if (vl_ato_auxiliar_w > 0) then
	vl_ato_auxiliar_w	:= (vl_ato_auxiliar_w / vl_item_p) * vl_saldo_w;
end if;

if (vl_ato_nao_cooperado_w > 0) then
	vl_ato_nao_cooperado_w	:=  (vl_ato_nao_cooperado_w / vl_item_p) * vl_saldo_w;
end if;

if	((vl_ato_cooperado_w + vl_ato_auxiliar_w + vl_ato_nao_cooperado_w) <> vl_saldo_w) then
	if (vl_ato_cooperado_w > 0) then
		vl_ato_cooperado_w	:= vl_saldo_w - vl_ato_auxiliar_w - vl_ato_nao_cooperado_w;
	elsif (vl_ato_auxiliar_w > 0) then
		vl_ato_auxiliar_w	:= vl_saldo_w - vl_ato_cooperado_w - vl_ato_nao_cooperado_w;
	elsif (vl_ato_nao_cooperado_w > 0) then
		vl_ato_nao_cooperado_w	:= vl_saldo_w - vl_ato_cooperado_w - vl_ato_auxiliar_w;
	end if;
end if;

if (ie_encerrou_saldo_item_p = 'S') then
	if	((vl_ato_cooperado_w + vl_baixa_ato_coop_p) <> vl_ato_cooperado_p) then
		vl_ato_cooperado_w	:= (vl_ato_cooperado_p - (vl_ato_cooperado_w + vl_baixa_ato_coop_p)) + vl_ato_cooperado_w;
	end if;
	
	if	((vl_ato_auxiliar_w + vl_baixa_ato_auxiliar_p) <> vl_ato_auxiliar_p) then
		vl_ato_auxiliar_w	:= (vl_ato_auxiliar_p - (vl_ato_auxiliar_w + vl_baixa_ato_auxiliar_p)) + vl_ato_auxiliar_w;
	end if;
	
	if	((vl_ato_nao_cooperado_w + vl_baixa_ato_nao_coop_p) <> vl_ato_nao_cooperado_p) then
		vl_ato_nao_cooperado_w	:= (vl_ato_nao_cooperado_p - (vl_ato_nao_cooperado_w + vl_baixa_ato_nao_coop_p)) + vl_ato_nao_cooperado_w;
	end if;

end if;

vl_ato_cooperado_p	:= vl_ato_cooperado_w;
vl_ato_nao_cooperado_p	:= vl_ato_nao_cooperado_w;
vl_ato_auxiliar_p	:= vl_ato_auxiliar_w;

END;

