CREATE OR REPLACE FUNCTION pls_mens_itens_limite_pck.obter_excecao_copartic ( nr_seq_tipo_copartic_p pls_regra_coparticipacao.nr_seq_tipo_coparticipacao%type, indice_regra_limite_p bigint) RETURNS varchar AS $body$
BEGIN

if (current_setting('pls_mens_itens_limite_pck.regra_limite_item_w')::vetor.count > 0) then
	for i in current_setting('pls_mens_itens_limite_pck.regra_limite_item_w')::vetor.first..regra_limite_item_w.last loop
		begin
		-- Verifica se a regra de excecao pertence a um item de coparticipacao

		if (current_setting('pls_mens_itens_limite_pck.regra_limite_item_w')::vetor[i].nr_seq_regra = current_setting('pls_mens_itens_limite_pck.regra_limite_item_w')::vetor[indice_regra_limite_p].nr_seq_regra) then
			if (current_setting('pls_mens_itens_limite_pck.regra_limite_item_w')::vetor[i].regra_excecao_copartic.count > 0) then
				for j in current_setting('pls_mens_itens_limite_pck.regra_limite_item_w')::vetor[i].regra_excecao_copartic.first..regra_limite_item_w[i].regra_excecao_copartic.last loop
					begin
					-- Verifica se o tipo de coparticipacao da regra de excecao e igual ao tipo de coparticipacao da regra que esta vinculada na conta

					if (current_setting('pls_mens_itens_limite_pck.regra_limite_item_w')::vetor[i].regra_excecao_copartic(j).nr_seq_tipo_coparticipacao = nr_seq_tipo_copartic_p) then
						return	'S';
					end if;
					end;
				end loop;
			end if;
		end if;
		end;
	end loop;
end if;
return 'N';
END;

