CREATE OR REPLACE FUNCTION pls_mens_itens_limite_pck.obter_valor_regra_limite ( indice_regra_limite_p bigint, vl_regra_limite_p bigint, ie_atualizar_valor_p text) RETURNS bigint AS $body$
DECLARE

					
vl_lib_regra_limite_w		double precision;
					

BEGIN

if	(vl_regra_limite_p <> 0 AND indice_regra_limite_p IS NOT NULL AND indice_regra_limite_p::text <> '') then
	if	((current_setting('pls_mens_itens_limite_pck.regra_limite_item_w')::vetor[indice_regra_limite_p].vl_item_gerado_mes + vl_regra_limite_p) > current_setting('pls_mens_itens_limite_pck.regra_limite_item_w')::vetor[indice_regra_limite_p].vl_maximo_item) then
		vl_lib_regra_limite_w	:= current_setting('pls_mens_itens_limite_pck.regra_limite_item_w')::vetor[indice_regra_limite_p].vl_maximo_item - current_setting('pls_mens_itens_limite_pck.regra_limite_item_w')::vetor[indice_regra_limite_p].vl_item_gerado_mes;
	end if;
end if;

if	(ie_atualizar_valor_p = 'S' AND indice_regra_limite_p IS NOT NULL AND indice_regra_limite_p::text <> '') then
	CALL pls_mens_itens_limite_pck.add_valor_regra(indice_regra_limite_p, coalesce(vl_lib_regra_limite_w, vl_regra_limite_p));
end if;

