 --Variavel para identificar se foi inserido algum item de mensalidade para o beneficiario
CREATE OR REPLACE FUNCTION pls_mens_itens_pck.obter_se_gera_item_suspensao ( ie_tipo_item_p pls_mensalidade_seg_item.ie_tipo_item%type, nr_seq_motivo_susp_p pls_motivo_susp_cobr_mens.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

	
qt_excecao_item_w	integer;


BEGIN

if (current_setting('pls_mens_itens_pck.nr_seq_motivo_susp_w')::pls_motivo_susp_cobr_mens.nr_sequencia%(type IS NOT NULL AND type::text <> '')) then
	select	count(1)
	into STRICT	qt_excecao_item_w
	from	pls_susp_cobr_mens_excecao a,
		pls_motivo_susp_cobr_mens b
	where	a.nr_seq_motivo = b.nr_sequencia
	and	a.nr_seq_motivo = nr_seq_motivo_susp_p
	and	a.ie_tipo_item  = ie_tipo_item_p
	and	b.ie_situacao   = 'A';
	
	if (qt_excecao_item_w = 0) then
		return 'N';
	else
		return 'S';
	end if;
end if;

return 'S';

END;

