CREATE OR REPLACE FUNCTION pls_mens_itens_pck.obter_se_item_gerado ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, ie_tipo_item_p pls_mensalidade_seg_item.ie_tipo_item%type, dt_inicio_cobertura_p pls_mensalidade_seg_item.dt_inicio_cobertura%type, dt_fim_cobertura_p pls_mensalidade_seg_item.dt_fim_cobertura%type, nr_seq_centro_apropriacao_p pls_centro_apropriacao.nr_sequencia%type) RETURNS varchar AS $body$
BEGIN

if (current_setting('pls_mens_itens_pck.tb_itens_gerados_mes_w')::tb_itens_gerados_mes.count > 0) then
