		if (current_setting('pls_mens_itens_pck.tb_itens_gerados_mes_w')::tb_itens_gerados_mes[i].ie_tipo_item = ie_tipo_item_p) then
			if	((nr_seq_centro_apropriacao_p IS NOT NULL AND nr_seq_centro_apropriacao_p::text <> '') and (current_setting('pls_mens_itens_pck.tb_apropriacoes_geradas_mes_w')::tb_apropriacoes_geradas_mes.count > 0) and (ie_tipo_item_p in ('1','5','25'))) then
				for y in current_setting('pls_mens_itens_pck.tb_apropriacoes_geradas_mes_w')::tb_apropriacoes_geradas_mes.first..tb_apropriacoes_geradas_mes_w.last loop
					begin
					if	((current_setting('pls_mens_itens_pck.tb_apropriacoes_geradas_mes_w')::tb_apropriacoes_geradas_mes[y].nr_seq_centro_apropriacao = nr_seq_centro_apropriacao_p) and (current_setting('pls_mens_itens_pck.tb_apropriacoes_geradas_mes_w')::tb_apropriacoes_geradas_mes[y].ie_tipo_item = ie_tipo_item_p)) then
						return	'S';
					end if;
					end;
				end loop;
			elsif (ie_tipo_item_p in ('1', '25', '5')) and (current_setting('pls_mens_itens_pck.tb_itens_gerados_mes_w')::tb_itens_gerados_mes[i](.dt_inicio_cobertura IS NOT NULL AND .dt_inicio_cobertura::text <> '') and current_setting('pls_mens_itens_pck.tb_itens_gerados_mes_w')::tb_itens_gerados_mes[i](.dt_fim_cobertura IS NOT NULL AND .dt_fim_cobertura::text <> '')) then
				if (dt_inicio_cobertura_p <= current_setting('pls_mens_itens_pck.tb_itens_gerados_mes_w')::tb_itens_gerados_mes[i].dt_fim_cobertura and dt_fim_cobertura_p >= current_setting('pls_mens_itens_pck.tb_itens_gerados_mes_w')::tb_itens_gerados_mes[i].dt_inicio_cobertura) then
					return	'S';
