			if (ds_lista_tipo_item_p IS NOT NULL AND ds_lista_tipo_item_p::text <> '') then
				if (obter_se_contido(current_setting('pls_mens_itens_pck.tb_ie_tipo_item_w')::pls_util_cta_pck.t_varchar2_table_5(i), ds_lista_tipo_item_p) = 'S') then
