CREATE OR REPLACE PROCEDURE pls_mens_itens_preco_pre_pck.gerar_item_apropriacao ( ie_tipo_item_p pls_mensalidade_seg_item.ie_tipo_item%type, nr_seq_seg_preco_p pls_segurado_preco.nr_sequencia%type, vl_preco_p bigint, vl_preco_item_p bigint) AS $body$
DECLARE

		
ie_preco_nao_subsidiado_w	varchar(1);
		
C01 CURSOR(	nr_seq_segurado_preco_pc	pls_segurado_preco.nr_sequencia%type) FOR
	SELECT	a.nr_seq_centro_apropriacao,
		(SELECT	coalesce(x.ie_valor_nao_subsidiado, 'N')
		from	pls_centro_apropriacao x
		where	x.nr_sequencia = a.nr_seq_centro_apropriacao) ie_valor_nao_subsidiado
	from	pls_segurado_preco_aprop a
	where	a.nr_seq_segurado_preco = nr_seq_segurado_preco_pc;

BEGIN

CALL pls_mens_itens_preco_pre_pck.limpar_vetor_apropriacao();

for r_c01_w in c01(nr_seq_segurado_preco_p) loop
	begin
	if (pls_mens_itens_pck.obter_se_pagador_item(nr_seq_segurado_p, nr_seq_pagador_p, ie_tipo_item_p, null, ie_pce_proporcional_p, r_c01_w.nr_seq_centro_apropriacao) = 'S') and
		(((ie_pce_proporcional_w = 'N') and (pls_mens_itens_pck.obter_se_item_gerado(nr_seq_segurado_p, ie_tipo_item_p, dt_inicio_cobertura_p, dt_fim_cobertura_p, r_c01_w.nr_seq_centro_apropriacao) = 'N')) or
		(ie_pce_proporcional_w = 'S' AND ie_item_pre_pce_w = 'N')) then	
		vl_centro_apropriacao_w	:= 0;
		
		if	(ie_tipo_item_p = '1' AND r_c01_w.ie_valor_nao_subsidiado = 'S') then
			ie_preco_nao_subsidiado_w	:= 'S';
		else
			ie_preco_nao_subsidiado_w 	:= 'N';
		end if;
		
		vl_centro_apropriacao_w := pls_mens_itens_preco_pre_pck.inserir_apropriacao_preco(ie_tipo_item_p, vl_preco_item_p, nr_seq_seg_preco_p, vl_preco_p, tx_proporcional_mens_w, r_c01_w.nr_seq_centro_apropriacao, ie_preco_nao_subsidiado_w, vl_centro_apropriacao_w);

		if (ie_preco_nao_subsidiado_w = 'S') then
			vl_total_apropriacao_nao_sub_w  := vl_total_apropriacao_nao_sub_w + vl_centro_apropriacao_w;
			ie_pagador_aprop_nao_sub_w	:= 'S';
		else
			vl_total_apropriacao_w	:= vl_total_apropriacao_w + vl_centro_apropriacao_w;
			ie_pagador_apropriacao_item_w	:= 'S';
		end if;
	end if;	
	end;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
