CREATE OR REPLACE FUNCTION pls_mens_pagador_pck.obter_se_restricao_uma_mens ( nr_seq_pagador_p pls_contrato_pagador.nr_sequencia%type, nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_seq_intercambio_p pls_intercambio.nr_sequencia%type, ie_tipo_estipulante_p text, dt_referencia_p pls_lote_mensalidade.dt_mesano_referencia%type) RETURNS varchar AS $body$
DECLARE

qt_registro_w			integer;
qt_regra_w			integer;
ie_restringe_uma_mens_w		pls_regra_mens_contrato.ie_restringe_uma_mens_pag_mes%type;

BEGIN

select	count(1)
into STRICT	qt_regra_w
from	pls_regra_mens_contrato
where	ie_tipo_regra	= 'G'
and	ie_restringe_uma_mens_pag_mes = 'S'
and	dt_inicio_vigencia <= dt_referencia_p
and (dt_fim_vigencia >= dt_referencia_p or coalesce(dt_fim_vigencia::text, '') = '');

if (qt_regra_w > 0) then
	
	select	coalesce(max(ie_restringe_uma_mens_pag_mes),'N')
	into STRICT	ie_restringe_uma_mens_w
	from	pls_regra_mens_contrato
	where	ie_tipo_regra	= 'G'
	and (ie_pessoa_contrato = ie_tipo_estipulante_p or ie_pessoa_contrato = 'A')
	and	dt_inicio_vigencia <= dt_referencia_p
	and (dt_fim_vigencia >= dt_referencia_p or coalesce(dt_fim_vigencia::text, '') = '');
	
	if (ie_restringe_uma_mens_w = 'S') then
		select	count(1)
		into STRICT	qt_registro_w
		from	pls_mensalidade
		where	nr_seq_pagador	= nr_seq_pagador_p
		and	dt_referencia	= dt_referencia_p
		and	coalesce(ie_cancelamento::text, '') = '';
		
		if (qt_registro_w > 0) then
			return 'S';
		else
			return 'N';
		end if;
	else
		return 'N';
	end if;
else
	return 'N';
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
