CREATE OR REPLACE FUNCTION pls_mens_processo_pck.obter_valor_bonific_vetor ( nr_seq_mensalidade_seg_p pls_mensalidade_segurado.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE

					
vl_total_bonif_w	pls_mensalidade_seg_item.vl_item%type;

BEGIN

select	coalesce(sum(vl_item), 0) vl_item
into STRICT	vl_total_bonif_w
from	pls_mensalidade_seg_item
where	nr_seq_mensalidade_seg = nr_seq_mensalidade_seg_p
and	ie_tipo_item = '14';

if (current_setting('pls_mens_processo_pck.tb_nr_seq_mensalidade_seg_w')::pls_util_cta_pck.t_number_table.count > 0) then
	for i in current_setting('pls_mens_processo_pck.tb_nr_seq_mensalidade_seg_w')::pls_util_cta_pck.t_number_table.first..tb_nr_seq_mensalidade_seg_w.last loop
		if (current_setting('pls_mens_processo_pck.tb_nr_seq_mensalidade_seg_w')::pls_util_cta_pck.t_number_table(i) = nr_seq_mensalidade_seg_p) then
			vl_total_bonif_w := vl_total_bonif_w + current_setting('pls_mens_processo_pck.tb_vl_item_w')::pls_util_cta_pck.t_number_table(i);
		end if;
	end loop;
end if;

return vl_total_bonif_w;

END;

$body$
