SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_mens_selecao_benef_pck.excluir_benef_excecao () AS $body$
DECLARE


qt_excecao_w	integer;

C01 CURSOR FOR
	SELECT	nr_seq_contrato,
		nr_seq_pagador,
		nr_seq_classif_itens,
		nr_seq_empresa,
		nr_seq_segurado,
		nr_seq_grupo_preco,
		nr_seq_grupo_intercambio,
		nr_seq_grupo_contrato
	from	pls_lote_mens_restricao
	where	nr_seq_lote	= current_setting('pls_mens_selecao_benef_pck.pls_lote_mensalidade_w')::pls_lote_mensalidade%rowtype.nr_sequencia;

BEGIN

select	count(1)
into STRICT	qt_excecao_w
from	pls_lote_mens_restricao
where	nr_seq_lote	= current_setting('pls_mens_selecao_benef_pck.pls_lote_mensalidade_w')::pls_lote_mensalidade%rowtype.nr_sequencia;

if (qt_excecao_w > 0) then
	for r_c01_w in C01 loop
		begin
		if (r_c01_w.nr_seq_contrato IS NOT NULL AND r_c01_w.nr_seq_contrato::text <> '') then
			delete	from	pls_mens_benef_pag_tmp a
			where	exists (SELECT	1
					from	pls_segurado x
					where	x.nr_sequencia	= a.nr_seq_segurado
					and	x.nr_seq_contrato = r_c01_w.nr_seq_contrato);
		end if;
		
		if (r_c01_w.nr_seq_pagador IS NOT NULL AND r_c01_w.nr_seq_pagador::text <> '') then
			delete	from	pls_mens_benef_pag_tmp
			where	nr_seq_pagador = r_c01_w.nr_seq_pagador;
		end if;
		
		if (r_c01_w.nr_seq_classif_itens IS NOT NULL AND r_c01_w.nr_seq_classif_itens::text <> '') then
			delete	from	pls_mens_benef_pag_tmp a
			where	exists (SELECT	1
					from	pls_contrato_pagador x
					where	x.nr_sequencia	= a.nr_seq_pagador
					and	x.nr_seq_classif_itens = r_c01_w.nr_seq_classif_itens);
		end if;
		
		if (r_c01_w.nr_seq_empresa IS NOT NULL AND r_c01_w.nr_seq_empresa::text <> '') then
			delete	from	pls_mens_benef_pag_tmp a
			where	exists (SELECT	1
					from	pls_contrato_pagador x,
						pls_contrato_pagador_fin y
					where	x.nr_sequencia	= y.nr_seq_pagador
					and	x.nr_sequencia	= a.nr_seq_pagador
					and	y.nr_seq_empresa = r_c01_w.nr_seq_empresa);
		end if;
		
		if (r_c01_w.nr_seq_segurado IS NOT NULL AND r_c01_w.nr_seq_segurado::text <> '') then
			delete	from	pls_mens_benef_pag_tmp
			where	nr_seq_segurado = r_c01_w.nr_seq_segurado;
		end if;
		
		if (r_c01_w.nr_seq_grupo_preco IS NOT NULL AND r_c01_w.nr_seq_grupo_preco::text <> '') then
			delete	from	pls_mens_benef_pag_tmp a
			where	exists (SELECT	1
					from	pls_segurado x,
						pls_contrato y,
						pls_preco_contrato z
					where	y.nr_sequencia	= x.nr_seq_contrato
					and	y.nr_sequencia	= z.nr_seq_contrato
					and	x.nr_sequencia	= a.nr_seq_segurado
					and	z.nr_seq_grupo	= r_c01_w.nr_seq_grupo_preco);
		end if;
		
		if (r_c01_w.nr_seq_grupo_intercambio IS NOT NULL AND r_c01_w.nr_seq_grupo_intercambio::text <> '') then
			delete	from	pls_mens_benef_pag_tmp a
			where	exists (SELECT	1
					from	pls_segurado x,
						pls_intercambio y
					where	y.nr_sequencia	= x.nr_seq_intercambio
					and	x.nr_sequencia	= a.nr_seq_segurado
					and	y.nr_seq_grupo_intercambio	= r_c01_w.nr_seq_grupo_intercambio);
		end if;
		
		if (r_c01_w.nr_seq_grupo_contrato IS NOT NULL AND r_c01_w.nr_seq_grupo_contrato::text <> '') then
			delete	from	pls_mens_benef_pag_tmp a
			where	exists (SELECT	1
					from	pls_segurado x,
						pls_contrato y,
						pls_contrato_grupo z
					where	y.nr_sequencia	= x.nr_seq_contrato
					and	y.nr_sequencia	= z.nr_seq_contrato
					and	x.nr_sequencia	= a.nr_seq_segurado
					and	z.nr_seq_grupo	= r_c01_w.nr_seq_grupo_contrato);
		end if;
		
		end;
	end loop;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
