SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_mensalidade_util_pck.ajustar_primeira_mens_pagador () AS $body$
DECLARE

tb_nr_seq_pagador_w	pls_util_cta_pck.t_number_table;
qt_titulo_pagador_w	bigint;
nr_indice_w		integer;
tb_nr_seq_pagador_update_w	pls_util_cta_pck.t_number_table;
tb_ie_primeira_mens_ger_w	pls_util_cta_pck.t_varchar2_table_1;
C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_contrato_pagador	a
	where	coalesce(a.ie_primeira_mensalidade_gerada::text, '') = '';
BEGIN
nr_indice_w	:= 0;
tb_nr_seq_pagador_update_w.delete;
tb_ie_primeira_mens_ger_w.delete;

open C01;
loop
	fetch C01 bulk collect into tb_nr_seq_pagador_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_pagador_w.count = 0;
	
	-- Se encontrou algum registro faz o update

	for i in tb_nr_seq_pagador_w.first..tb_nr_seq_pagador_w.last loop
		select	count(1)
		into STRICT	qt_titulo_pagador_w
		from	titulo_receber
		where	nr_seq_pagador = tb_nr_seq_pagador_w(i)
		and	(nr_seq_mensalidade IS NOT NULL AND nr_seq_mensalidade::text <> '');
		
		tb_nr_seq_pagador_update_w(nr_indice_w)	:= tb_nr_seq_pagador_w(i);
		if (qt_titulo_pagador_w > 0) then
			tb_ie_primeira_mens_ger_w(nr_indice_w)	:= 'S';
		else
			tb_ie_primeira_mens_ger_w(nr_indice_w)	:= 'N';
		end if;
		
		if (nr_indice_w >= pls_util_pck.qt_registro_transacao_w) then
			forall j in tb_nr_seq_pagador_update_w.first..tb_nr_seq_pagador_update_w.last
				update 	pls_contrato_pagador
				set	ie_primeira_mensalidade_gerada	= tb_ie_primeira_mens_ger_w(j)
				where	nr_sequencia = tb_nr_seq_pagador_update_w(j);
			commit;
			
			nr_indice_w	:= 0;
			tb_nr_seq_pagador_update_w.delete;
			tb_ie_primeira_mens_ger_w.delete;
		else
			nr_indice_w	:= nr_indice_w + 1;
		end if;
	end loop;
end loop;

if (tb_nr_seq_pagador_update_w.count > 0) then
	forall j in tb_nr_seq_pagador_update_w.first..tb_nr_seq_pagador_update_w.last
		update 	pls_contrato_pagador
		set	ie_primeira_mensalidade_gerada	= tb_ie_primeira_mens_ger_w(j)
		where	nr_sequencia = tb_nr_seq_pagador_update_w(j);
	commit;
end if;
close C01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_mensalidade_util_pck.ajustar_primeira_mens_pagador () FROM PUBLIC;
