CREATE OR REPLACE PROCEDURE pls_mensalidade_util_pck.ajustar_situacao_plano_interc () AS $body$
DECLARE

tb_nr_seq_interc_plano_w	pls_util_cta_pck.t_number_table;
C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_intercambio_plano
	where	coalesce(ie_situacao::text, '') = '';

BEGIN

--Limpa a tabela

tb_nr_seq_interc_plano_w.delete;
--Abre o cursor

open C01;
loop
	fetch C01 bulk collect into tb_nr_seq_interc_plano_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_interc_plano_w.count = 0;
	
	-- Se encontrou algum registro faz o update

	forall i in tb_nr_seq_interc_plano_w.first..tb_nr_seq_interc_plano_w.last
		update 	pls_intercambio_plano
		set	ie_situacao = 'A'
		where	nr_sequencia = tb_nr_seq_interc_plano_w(i);
	commit;
end loop;
close C01;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
