SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_mensalidade_util_pck.obter_regra_geral ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_seq_intercambio_p pls_intercambio.nr_sequencia%type, ie_tipo_estipulante_p text, dt_referencia_p timestamp, ie_mensalidade_mes_rescisao_p INOUT pls_regra_mens_contrato.ie_mensalidade_mes_rescisao%type, ie_rescisao_proporcional_p INOUT pls_regra_mens_contrato.ie_rescisao_proporcional%type) AS $body$
DECLARE

nr_seq_regra_w				pls_regra_mens_contrato.nr_sequencia%type;
ie_mensalidade_mes_rescisao_w		pls_regra_mens_contrato.ie_mensalidade_mes_rescisao%type;
ie_rescisao_proporcional_w		pls_regra_mens_contrato.ie_rescisao_proporcional%type;

BEGIN
nr_seq_regra_w	:= null;
if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then
	select	max(nr_sequencia),
		max(ie_mensalidade_mes_rescisao),
		max(ie_rescisao_proporcional)
	into STRICT	nr_seq_regra_w,
		ie_mensalidade_mes_rescisao_w,
		ie_rescisao_proporcional_w
	from	pls_regra_mens_contrato
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	ie_tipo_regra	= 'G'
	and	dt_referencia_p between dt_inicio_vigencia_ref and dt_fim_vigencia_ref;
elsif (nr_seq_intercambio_p IS NOT NULL AND nr_seq_intercambio_p::text <> '') then
	select	max(nr_sequencia),
		max(ie_mensalidade_mes_rescisao),
		max(ie_rescisao_proporcional)
	into STRICT	nr_seq_regra_w,
		ie_mensalidade_mes_rescisao_w,
		ie_rescisao_proporcional_w
	from	pls_regra_mens_contrato
	where	nr_seq_intercambio	= nr_seq_intercambio_p
	and	ie_tipo_regra	= 'G'
	and	dt_referencia_p between dt_inicio_vigencia_ref and dt_fim_vigencia_ref;
end if;

if (coalesce(nr_seq_regra_w::text, '') = '') then
	if (ie_tipo_estipulante_p = 'PF') then
		ie_mensalidade_mes_rescisao_w	:= current_setting('pls_mensalidade_util_pck.ie_mensalidade_mes_resc_pf_w')::pls_regra_mens_contrato.ie_mensalidade_mes_rescisao%type;
		ie_rescisao_proporcional_w	:= current_setting('pls_mensalidade_util_pck.ie_rescisao_proporcional_pf_w')::pls_regra_mens_contrato.ie_rescisao_proporcional%type;
	elsif (ie_tipo_estipulante_p = 'PJ') then
		ie_mensalidade_mes_rescisao_w	:= current_setting('pls_mensalidade_util_pck.ie_mensalidade_mes_resc_pj_w')::pls_regra_mens_contrato.ie_mensalidade_mes_rescisao%type;
		ie_rescisao_proporcional_w	:= current_setting('pls_mensalidade_util_pck.ie_rescisao_proporcional_pj_w')::pls_regra_mens_contrato.ie_rescisao_proporcional%type;
	end if;
	
	if (coalesce(ie_mensalidade_mes_rescisao_w::text, '') = '') and (coalesce(ie_rescisao_proporcional_w::text, '') = '') then
		ie_mensalidade_mes_rescisao_w	:= current_setting('pls_mensalidade_util_pck.ie_mensalidade_mes_resc_a_w')::pls_regra_mens_contrato.ie_mensalidade_mes_rescisao%type;
		ie_rescisao_proporcional_w	:= current_setting('pls_mensalidade_util_pck.ie_rescisao_proporcional_a_w')::pls_regra_mens_contrato.ie_rescisao_proporcional%type;
	end if;
end if;
ie_mensalidade_mes_rescisao_p		:= coalesce(ie_mensalidade_mes_rescisao_w,'N');
ie_rescisao_proporcional_p		:= coalesce(ie_rescisao_proporcional_w,'N');

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
