CREATE OR REPLACE PROCEDURE pls_mensalidade_util_pck.obter_regra_mens_futura ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_seq_intercambio_p pls_intercambio.nr_sequencia%type, ie_tipo_estipulante_p text, dt_referencia_p timestamp, qt_interv_mes_mensalidade_p INOUT pls_regra_mens_contrato.qt_interv_mes_mensalidade%type, ie_agrupar_valor_p INOUT pls_regra_mens_contrato.ie_agrupar_valor%type) AS $body$
DECLARE

nr_seq_regra_w			pls_regra_mens_contrato.nr_sequencia%type;
qt_interv_mes_mensalidade_w	pls_regra_mens_contrato.qt_interv_mes_mensalidade%type;
ie_agrupar_valor_w		pls_regra_mens_contrato.ie_agrupar_valor%type;

BEGIN
nr_seq_regra_w	:= null;
if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then
	select	max(nr_sequencia),
		max(qt_interv_mes_mensalidade),
		max(ie_agrupar_valor)
	into STRICT	nr_seq_regra_w,
		qt_interv_mes_mensalidade_w,
		ie_agrupar_valor_w
	from	pls_regra_mens_contrato
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	ie_tipo_regra	= 'F'
	and	dt_referencia_p between dt_inicio_vigencia_ref and dt_fim_vigencia_ref;
elsif (nr_seq_intercambio_p IS NOT NULL AND nr_seq_intercambio_p::text <> '') then
	select	max(nr_sequencia),
		max(qt_interv_mes_mensalidade),
		max(ie_agrupar_valor)
	into STRICT	nr_seq_regra_w,
		qt_interv_mes_mensalidade_w,
		ie_agrupar_valor_w
	from	pls_regra_mens_contrato
	where	nr_seq_intercambio	= nr_seq_intercambio_p
	and	ie_tipo_regra	= 'F'
	and	dt_referencia_p between dt_inicio_vigencia_ref and dt_fim_vigencia_ref;
end if;

if (coalesce(nr_seq_regra_w::text, '') = '') then
	if (ie_tipo_estipulante_p = 'PF') then
		qt_interv_mes_mensalidade_w	:= current_setting('pls_mensalidade_util_pck.qt_interv_mes_mensalidade_pf_w')::pls_regra_mens_contrato.qt_interv_mes_mensalidade%type;
		ie_agrupar_valor_w		:= current_setting('pls_mensalidade_util_pck.ie_agrupar_valor_pf_w')::pls_regra_mens_contrato.ie_agrupar_valor%type;
	elsif (ie_tipo_estipulante_p = 'PJ') then
		qt_interv_mes_mensalidade_w	:= current_setting('pls_mensalidade_util_pck.qt_interv_mes_mensalidade_pj_w')::pls_regra_mens_contrato.qt_interv_mes_mensalidade%type;
		ie_agrupar_valor_w		:= current_setting('pls_mensalidade_util_pck.ie_agrupar_valor_pj_w')::pls_regra_mens_contrato.ie_agrupar_valor%type;
	end if;
	
	if (coalesce(qt_interv_mes_mensalidade_w::text, '') = '') and (coalesce(ie_agrupar_valor_w::text, '') = '') then
		qt_interv_mes_mensalidade_w	:= current_setting('pls_mensalidade_util_pck.qt_interv_mes_mensalidade_a_w')::pls_regra_mens_contrato.qt_interv_mes_mensalidade%type;
		ie_agrupar_valor_w		:= current_setting('pls_mensalidade_util_pck.ie_agrupar_valor_a_w')::pls_regra_mens_contrato.ie_agrupar_valor%type;
	end if;
end if;

qt_interv_mes_mensalidade_p	:= coalesce(qt_interv_mes_mensalidade_w,0);
ie_agrupar_valor_p		:= coalesce(ie_agrupar_valor_w,'N');

END;

$body$
