CREATE OR REPLACE PROCEDURE pls_mensalidade_util_pck.obter_regra_vl_minimo (nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_seq_intercambio_p pls_intercambio.nr_sequencia%type, ie_tipo_estipulante_p text, dt_referencia_p timestamp, nr_seq_regra_vl_minimo_p INOUT pls_regra_mens_contrato.nr_sequencia%type, vl_minimo_p INOUT pls_regra_mens_contrato.vl_minimo%type, vl_minimo_abat_fatura_p INOUT pls_regra_mens_contrato.vl_minimo_abat_fatura%type) AS $body$
DECLARE

nr_seq_regra_vl_minimo_w	pls_regra_mens_contrato.nr_sequencia%type;
vl_minimo_w			pls_regra_mens_contrato.vl_minimo%type;
vl_minimo_abat_fatura_w		pls_regra_mens_contrato.vl_minimo_abat_fatura%type;

BEGIN
nr_seq_regra_vl_minimo_w	:= null;
if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then
	select	max(nr_sequencia),
		max(vl_minimo),
		max(vl_minimo_abat_fatura)
	into STRICT	nr_seq_regra_vl_minimo_w,
		vl_minimo_w,
		vl_minimo_abat_fatura_w
	from	pls_regra_mens_contrato
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	ie_tipo_regra	= 'VM'
	and	dt_referencia_p between dt_inicio_vigencia_ref and dt_fim_vigencia_ref;
elsif (nr_seq_intercambio_p IS NOT NULL AND nr_seq_intercambio_p::text <> '') then
	select	max(nr_sequencia),
		max(vl_minimo),
		max(vl_minimo_abat_fatura)
	into STRICT	nr_seq_regra_vl_minimo_w,
		vl_minimo_w,
		vl_minimo_abat_fatura_w
	from	pls_regra_mens_contrato
	where	nr_seq_intercambio	= nr_seq_intercambio_p
	and	ie_tipo_regra	= 'VM'
	and	dt_referencia_p between dt_inicio_vigencia_ref and dt_fim_vigencia_ref;
end if;

if (coalesce(nr_seq_regra_vl_minimo_w::text, '') = '') then
	if (ie_tipo_estipulante_p = 'PF') then
		nr_seq_regra_vl_minimo_w	:= current_setting('pls_mensalidade_util_pck.nr_seq_regra_vl_minimo_pf_w')::pls_regra_mens_contrato.nr_sequencia%type;
		vl_minimo_w			:= current_setting('pls_mensalidade_util_pck.vl_minimo_pf_w')::pls_regra_mens_contrato.vl_minimo%type;
		vl_minimo_abat_fatura_w		:= current_setting('pls_mensalidade_util_pck.vl_minimo_abat_fatura_pf_w')::pls_regra_mens_contrato.vl_minimo_abat_fatura%type;
	elsif (ie_tipo_estipulante_p = 'PJ') then
		nr_seq_regra_vl_minimo_w	:= current_setting('pls_mensalidade_util_pck.nr_seq_regra_vl_minimo_pj_w')::pls_regra_mens_contrato.nr_sequencia%type;
		vl_minimo_w			:= current_setting('pls_mensalidade_util_pck.vl_minimo_pj_w')::pls_regra_mens_contrato.vl_minimo%type;
		vl_minimo_abat_fatura_w		:= current_setting('pls_mensalidade_util_pck.vl_minimo_abat_fatura_pj_w')::pls_regra_mens_contrato.vl_minimo_abat_fatura%type;
	end if;
	
	if (coalesce(nr_seq_regra_vl_minimo_w::text, '') = '') then
		nr_seq_regra_vl_minimo_w	:= current_setting('pls_mensalidade_util_pck.nr_seq_regra_vl_minimo_a_w')::pls_regra_mens_contrato.nr_sequencia%type;
		vl_minimo_w			:= current_setting('pls_mensalidade_util_pck.vl_minimo_a_w')::pls_regra_mens_contrato.vl_minimo%type;
		vl_minimo_abat_fatura_w		:= current_setting('pls_mensalidade_util_pck.vl_minimo_abat_fatura_a_w')::pls_regra_mens_contrato.vl_minimo_abat_fatura%type;
	end if;
end if;

nr_seq_regra_vl_minimo_p	:= nr_seq_regra_vl_minimo_w;
vl_minimo_p			:= coalesce(vl_minimo_w,0);
vl_minimo_abat_fatura_p		:= coalesce(vl_minimo_abat_fatura_w,0);

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_mensalidade_util_pck.obter_regra_vl_minimo (nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_seq_intercambio_p pls_intercambio.nr_sequencia%type, ie_tipo_estipulante_p text, dt_referencia_p timestamp, nr_seq_regra_vl_minimo_p INOUT pls_regra_mens_contrato.nr_sequencia%type, vl_minimo_p INOUT pls_regra_mens_contrato.vl_minimo%type, vl_minimo_abat_fatura_p INOUT pls_regra_mens_contrato.vl_minimo_abat_fatura%type) FROM PUBLIC;
