CREATE OR REPLACE PROCEDURE pls_monitor_qualidade_pck.consiste_lote ( nr_lote_qualidade_p pls_lote_qualida_monit_ans.nr_lote_monitoramento%type, dt_competencia_p text, ie_importa_p INOUT text) AS $body$
DECLARE

 
