CREATE OR REPLACE PROCEDURE pls_monitor_qualidade_pck.gera_conta_lote ( dados_conta_qualidade_p pls_monitor_qualidade_pck.dados_conta_qualidade, nm_usuario_p usuario.nm_usuario%type, nr_seq_cta_qualid_p INOUT pls_cta_qualid_monit_ans.nr_sequencia%type) AS $body$
DECLARE

				 
cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;
cd_cgc_w			pessoa_juridica.cd_cgc%type;
			

BEGIN 
if (dados_conta_qualidade_p.ie_tipo_prestador = '2') then 
 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	pessoa_fisica 
	where	nr_cpf = dados_conta_qualidade_p.cd_cnpj_cpf;
	 
else 
	cd_cgc_w := dados_conta_qualidade_p.cd_cnpj_cpf;
end if;
 
insert	into	pls_cta_qualid_monit_ans(	cd_cgc, cd_erro_ans, cd_guia_operadora, 
		cd_guia_prestador, cd_pessoa_fisica, cd_reembolso, 
		ds_conteudo_ans, dt_atualizacao, dt_atualizacao_nrec, 
		dt_processamento, dt_processamento_ans, ie_campo_ans, 
		ie_tp_prestador, nm_usuario, nm_usuario_nrec, 
		nr_seq_lote, nr_sequencia, cd_cnes) 
values (	cd_cgc_w, null, dados_conta_qualidade_p.cd_guia_operadora, 
		dados_conta_qualidade_p.cd_guia_prestador, cd_pessoa_fisica_w, dados_conta_qualidade_p.nr_seq_reembolso, 
		null, clock_timestamp(), clock_timestamp(), 
		null, null, null, 
		dados_conta_qualidade_p.ie_tipo_prestador, nm_usuario_p, nm_usuario_p, 
		dados_conta_qualidade_p.nr_seq_lote_qualidade, nextval('pls_cta_qualid_monit_ans_seq'), dados_conta_qualidade_p.cd_cnes) 
	returning nr_sequencia into nr_seq_cta_qualid_p;
		 
commit;
 
END;

