CREATE OR REPLACE PROCEDURE pls_mov_benef_imp_pck.alterar_status_lote ( nr_seq_lote_p pls_mov_benef_lote.nr_sequencia%type, ie_opcao_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/*
ie_opcao_p
'I' - Importado
'C' - Consistido/Inconsistente
'CO' - Contratado
*/
qt_inconst_w	bigint;

BEGIN
if (ie_opcao_p = 'I') then
	update	pls_mov_benef_lote
	set	ie_status = '1',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_lote_p;
elsif (ie_opcao_p = 'C') then
	select	count(1)
	into STRICT	qt_inconst_w
	from 	pls_mov_benef_lote_incons
	where	nr_seq_lote = nr_seq_lote_p;
	
		if (qt_inconst_w > 0 ) then		
			update	pls_mov_benef_lote
			set	ie_status = '3',
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
			where	nr_sequencia = nr_seq_lote_p;
		else
			update	pls_mov_benef_lote
			set	ie_status = '2',
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where	nr_sequencia = nr_seq_lote_p;
		end if;
elsif (ie_opcao_p = 'CO') then
	update	pls_mov_benef_lote
	set	ie_status = '4'
	where	nr_sequencia = nr_seq_lote_p;
end if;
			
END;

$body$
