CREATE OR REPLACE PROCEDURE pls_mov_benef_imp_pck.gerar_carencias_beneficiario ( nr_seq_mov_segurado_p pls_mov_benef_segurado.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

qt_carencia_w	integer;
C01 CURSOR FOR
	SELECT	a.dt_fim_carencia,
		b.nr_seq_segurado,
		(	SELECT	max(x.nr_sequencia)
			from	pls_tipo_carencia x
			where	x.cd_ptu = a.cd_tipo_cobertura) nr_seq_tipo_carencia
	from	pls_mov_benef_seg_carencia a,
		pls_mov_benef_segurado	b
	where	b.nr_sequencia	= a.nr_seq_mov_segurado
	and	b.nr_sequencia	= nr_seq_mov_segurado_p;
BEGIN

for r_c01_w in C01 loop
	begin
	
	select	count(1)
	into STRICT	qt_carencia_w
	from	pls_carencia
	where	nr_seq_segurado		= r_c01_w.nr_seq_segurado
	and	nr_seq_tipo_carencia	= r_c01_w.nr_seq_tipo_carencia;
	
	if (qt_carencia_w = 0) then
		insert	into	pls_carencia(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
				nr_seq_tipo_carencia, nr_seq_segurado, qt_dias,
				ds_observacao, dt_fim_vigencia )
			values (	nextval('pls_carencia_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
				r_c01_w.nr_seq_tipo_carencia, r_c01_w.nr_seq_segurado, 0,
				'Carência gerada a partir da movimentação cadastral de beneficiários', r_c01_w.dt_fim_carencia );
	end if;
	end;
end loop;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
