CREATE OR REPLACE PROCEDURE pls_mov_benef_imp_pck.gerar_planos_contrato ( nr_seq_lote_p pls_mov_benef_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

qt_registro_w	integer;
C01 CURSOR FOR
	SELECT	a.*,
		(SELECT	max(x.nr_seq_intercambio)
		from	pls_mov_benef_contrato x
		where	x.nr_seq_lote	= nr_seq_lote_p
		and	x.nr_contrato	= a.nr_contrato) nr_seq_intercambio
	from	pls_mov_benef_plano a
	where	a.nr_seq_lote = nr_seq_lote_p
	and	(a.nr_seq_plano IS NOT NULL AND a.nr_seq_plano::text <> '')
	and	(a.nr_contrato IS NOT NULL AND a.nr_contrato::text <> '');

BEGIN

for r_c01_w in C01 loop
	begin
	
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_intercambio_plano
	where	nr_seq_intercambio	= r_c01_w.nr_seq_intercambio
	and	nr_seq_plano		= r_c01_w.nr_seq_plano;
	
	if (qt_registro_w = 0) then
		insert	into	pls_intercambio_plano(	nr_sequencia, dt_atualizacao, nm_usuario,
				dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_intercambio,
				nr_seq_plano, nr_seq_tabela, ie_situacao,
				cd_plano_origem )
			values (	nextval('pls_intercambio_plano_seq'), clock_timestamp(), nm_usuario_p,
				clock_timestamp(), nm_usuario_p, r_c01_w.nr_seq_intercambio,
				r_c01_w.nr_seq_plano, null, 'A',
				r_c01_w.cd_plano_origem );
	end if;
	
	end;
end loop;

END;

$body$
