CREATE OR REPLACE PROCEDURE pls_mov_benef_imp_pck.gerar_sca_beneficiario ( nr_seq_mov_segurado_p pls_mov_benef_segurado.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	a.*,
		b.nr_seq_segurado,
		(	SELECT	max(nr_sequencia)
			from	pls_plano x
			where	x.cd_plano_intercambio	= a.cd_tipo_produto
			and	x.ie_tipo_operacao	= 'A') nr_seq_sca
	from	pls_mov_benef_seg_sca a,
		pls_mov_benef_segurado	b
	where	b.nr_sequencia	= a.nr_seq_mov_segurado
	and	b.nr_sequencia	= nr_seq_mov_segurado_p;
BEGIN
for r_c01_w in C01 loop
	begin
	
	if (r_c01_w.nr_seq_sca IS NOT NULL AND r_c01_w.nr_seq_sca::text <> '') then
		insert	into	pls_sca_vinculo(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
				nr_seq_segurado, nr_seq_plano, ds_observacao, dt_liberacao)
			values (	nextval('pls_sca_vinculo_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
				r_c01_w.nr_seq_segurado, r_c01_w.nr_seq_sca, 'SCA Gerada a partir da movimentação cadastral de beneficiários', clock_timestamp());
	end if;
	
	end;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
