	-- DESCARREGA O VETOR DE SEGURADOS
CREATE OR REPLACE PROCEDURE pls_mov_benef_pck.inserir_segurado ( ie_descarregar_vetor_p text) AS $body$
BEGIN

	if	((nr_indice_w >= pls_util_pck.qt_registro_transacao_w) or (ie_descarregar_vetor_p = 'S')) then
		if (tb_nr_seq_segurado_w.count > 0) then
			forall i in tb_nr_seq_segurado_w.first .. tb_nr_seq_segurado_w.last
			
				insert	into	pls_mov_benef_segurado(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_lote,
						nr_seq_mov_operadora,
						nr_seq_mov_contrato,
						nr_seq_segurado,
						cd_plano_intercambio,
						cd_plano_origem,
						nr_contrato,
						cd_usuario_plano,
						dt_validade_carteira,
						nr_via_carteira,
						nm_beneficiario,
						nm_abreviado,
						nm_social,
						nm_social_abreviado,
						dt_nascimento,
